import React, { Component } from 'react';
import axios from 'axios';
import Movie from "./Movie";

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isSelect: true, // 현재 로딩 여부
      date: props.date, // App.js의 date 값
      movies: [], // 영화 리스트 저장
      selectedMovieCd: null // 선택된 영화 코드
    };
  }

  getMovies = async () => { // 오픈API 값 받기
    const { date } = this.state;
    const url = "http://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=f6075493e17d4525078a94b31ec0ef70&targetDt=" + date;

    const { data: { boxOfficeResult: { dailyBoxOfficeList } } } = await axios.get(url);
    this.setState({ movies: dailyBoxOfficeList, isSelect: false });
  }

  componentDidMount() {
    this.getMovies();
  }

  handleMovieClick = (movieCd) => {
    this.setState({ selectedMovieCd: movieCd });
  }

  render() {
    const { isSelect, movies, selectedMovieCd } = this.state;

    return (
      <div>
        {/* 영화 리스트 */}
        {isSelect ? (
          <div>
            <h2>로딩중입니다.</h2>
            <p>Select.js의 현재 isSelect값 : false</p>
          </div>
        ) : (
          <div>
            {movies.map((movie) => (
              <Movie
                key={movie.movieCd}
                moviecd={movie.movieCd}
                title={movie.movieNm}
                audiCnt={movie.audiCnt}
                rank={movie.rank}
                showDetail={selectedMovieCd === movie.movieCd} // 선택된 영화만 상세 정보를 보여주도록 prop 전달
                onClick={this.handleMovieClick} // 영화를 클릭하면 해당 영화만 표시하도록 핸들러 전달
              />
            ))}
          </div>
        )}
      </div>
    );
  }
}

export default List;
