import React, { useState, useEffect } from "react";
import { movieDetailGetFetch } from "./apis/movieDetailGetFetch";
import { Table, Button } from "react-bootstrap";

const Detail = ({ movieId }) => {
  const [loading, setLoading] = useState(true);
  const [info, setInfo] = useState({});
  const [showExtraInfo, setShowExtraInfo] = useState(false);

  useEffect(() => {
    const fetchMovieDetail = async () => {
      try {
        const {
          data: {
            movieInfoResult: { movieInfo },
          },
        } = await movieDetailGetFetch(movieId);

        setInfo({ ...movieInfo });
        setLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovieDetail();
  }, [movieId]);

  const {
    actors,
    audits,
    directors,
    genres,
    movieNm,
    movieNmEn,
    prdtStatNm,
    openDt,
    typeNm,
  } = info;

  const handleToggleExtraInfo = () => {
    setShowExtraInfo(!showExtraInfo);
  };

  return loading ? (
    <div>로딩중입니다..</div>
  ) : (
    <div>
      <Table striped bordered hover>
        <tbody>
          <tr>
            <td>
              <strong>영화 제목</strong>
            </td>
            <td>
              {movieNm} ({movieNmEn})
            </td>
          </tr>
          <tr>
            <td>
              <strong>감독</strong>
            </td>
            <td>
              {directors?.map((director, i) => (
                <span key={i}>{director.peopleNm} </span>
              ))}
            </td>
          </tr>
          <tr>
            <td>
              <strong>관람 등급</strong>
            </td>
            <td>{audits?.map((rule) => rule.watchGradeNm)}</td>
          </tr>
          <tr>
            <td>
              <strong>개봉일</strong>
            </td>
            <td>
              {openDt && (
                <>
                  {openDt.substring(0, 4)}년 {openDt.substring(4, 6)}월{" "}
                  {openDt.substring(6, 8)}일 {prdtStatNm}
                </>
              )}
            </td>
          </tr>
          <tr>
            <td>
              <strong>장르</strong>
            </td>
            <td>
              {genres?.map((genre, i) => (
                <div key={i}>{genre.genreNm}</div>
              ))}
            </td>
          </tr>
          {showExtraInfo && (
            <tr>
              <td colSpan="2">
                <div>
                  <h3>배우들</h3>
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>이름</th>
                        <th>배역</th>
                      </tr>
                    </thead>
                    <tbody>
                      {actors?.map((actor, i) => (
                        <tr key={i}>
                          <td>{actor.peopleNm}</td>
                          <td>{actor.cast}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
                <div>
                  <strong>영화 유형:</strong> {typeNm}
                </div>
              </td>
            </tr>
          )}
        </tbody>
      </Table>

      <Button id="plusbutton" onClick={handleToggleExtraInfo}>
        {showExtraInfo ? "추가 정보 닫기" : "더보기"}
      </Button>
    </div>
  );
};

export default Detail;
