import MovieList from "./MovieList"; // 추가

function Movie({ movies }) { // 수정: moviecd, title, audiCnt, rank 제거
  const [showDetails, setShowDetails] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0); // 추가

  const handleShowDetails = () => {
    setShowDetails(true);
  };

  const handleNextMovie = () => { // 추가
    setCurrentIndex((prevIndex) => prevIndex + 1);
  };

  return (
    <div>
      <h2>영화제목: {movies[currentIndex].title} {movies[currentIndex].moviecd}</h2> {/* 수정: title, moviecd 변경 */}
      <h4>관객수: {movies[currentIndex].audiCnt}, 일간 박스오피스 랭킹: {movies[currentIndex].rank}</h4> {/* 수정: audiCnt, rank 변경 */}
      <button onClick={handleShowDetails}>상세정보</button>

      {showDetails ? (
        <MovieDetails movieId={movies[currentIndex].moviecd} />
      ) : (
        <Main moviecd2={movies[currentIndex].moviecd} />
      )}

      <MovieList movies={movies} currentIndex={currentIndex} /> {/* 추가 */}
      <button onClick={handleNextMovie}>다음 영화</button> {/* 추가 */}
    </div>
  );
}

export default Movie;
